<style lang="sass">
.assets
  &-interface
    display: flex
    margin-bottom: 40px
    &__el
      display: flex
      align-items: center
      font-size: clamp(10px, 0.8vw, 14px)
      outline: 0.2px solid #01B47E
      outline-offset: -1px
      border-radius: 20px
      padding: 8px 12px
      margin-right: 12px
      cursor: pointer
      &:hover
        background: #01B47E
        box-shadow: 0px 12px 24px 0px rgba(#01B47E, 0.20), 0px 4px 8px 0px rgba(#01B47E, 0.30)
      &_major
        outline: none
        background: linear-gradient(89deg, rgba(1, 180, 126, 0.60) 5.07%, rgba(126, 246, 178, 0.60) 99.37%)
        &:hover
          box-shadow: 0px 12px 24px 0px rgba(0, 150, 105, 0.20), 0px 4px 8px 0px rgba(100, 232, 159, 0.30)

    &__icon
      margin-right: 10px

    &__title
      color: #fff

</style>
